import { buildHeaderGroups } from "@tanstack/react-table";
import React from "react";
import { useEffect, useState } from "react";

export default function UseFetch() {
  const [data, setData] = useState({});

  const url = "https://api.data.gov.sg/v1/environment/psi";

  // React.useEffect(() => {
  //   const fetchData = async () => {
  //       const res = await fetch(url);
  //       const json = await res.json();
  //       setResponse(json.items[0].readings);

  //       return (
  //         <table>
  //           <thead>
  //             <tr>
  //                 { Object.keys(response).forEach((key) => <th>{key}</th> )}
  //             </tr>
  //           </thead>
  //         </table>
  //       )
  //   };
  //   fetchData();
  // }, []);
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState()

  const [headers, setHeaders] = useState([])
  const [rows, setRows] = useState([])
  let headersArray = []
  let rowsArray = []

  const [headersHtml, setHeadersHtml] = useState([])
  let headersHtmlArray = []
  let rowsHtmlArray = []

  useEffect(() => {
    fetch(url)
    .then((json) => json.json())
    .then((res) => {
      setData(res.items[0].readings)
      setLoading(false)
    }).catch(error => setError(error))
  }, []);

  useEffect(() => {
    headersArray = []
    rowsArray = []
    Object.keys(data).forEach((key) => headersArray.push(key))
    Object.values(data).forEach((values) => rowsArray.push(values))
    setHeaders(headersArray)
    setRows(rowsArray)
  }, [data])

  useEffect(() => {
    headersHtmlArray = []
    for (const i in headers) {
      headersHtmlArray.push(<th>{headers[i]}</th>)
    }
    setHeadersHtml(headersHtmlArray)
  }, [headers])

  useEffect(() => {
    rowsHtmlArray = []
    for (const i in rows) {
      rowsHtmlArray.push(<tr>{headers[i]}</tr>)
    }
    setHeadersHtml(headersHtmlArray)
  }, [rows])


  if (loading) return <div>loading</div>

  if (error) return <div>error</div>

  console.log(headersHtml)

  return (
    <div>
        <table style={{border: "black"}}>
        <thead>
          <tr>
          {headersHtml}
          </tr>
        </thead>
        <tbody>

        </tbody>
      </table>
    </div>
  )  
}
